sys_ui_script.script:
(function () {
    angular.module('angularapp').service('utilFactory', [
        function () {
			this.escapeXml = function (unsafe) {
				return unsafe.replace(/[<>&'"]/g, function (c) {
					switch (c) {
						case '<': return '&lt;';
						case '>': return '&gt;';
						case '&': return '&amp;';
						case '\'': return '&apos;';
						case '"': return '&quot;';
					}
				});
			};
        }
    ]);
})();
