sys_ui_script.script:
(function(){
angular.module('angularapp').service('toasterservice', [
	'toaster', 
	function(toaster) {
		this.showErrorToast = function (title, msg, timeout) {
			if(!timeout) {
				timeout = 3000;
			}
			toaster.pop({
				type: 'error',
				title: title,
				body: msg,
				timeout: timeout
			});
		};

		this.showInfoToast = function (title, msg, timeout) {
			if(!timeout) {
				timeout = 3000;
			}
			toaster.pop({
				type: 'info',
				title: title,
				body: msg,
				timeout: timeout
			});
		};

		this.showSuccessToast = function (title, msg, timeout) {
			if(!timeout) {
				timeout = 3000;
			}
			toaster.pop({
				type: 'success',
				title: title,
				body: msg,
				timeout: timeout
			});
		};

		this.showWarningToast = function (title, msg, timeout) {
			if(!timeout) {
				timeout = 3000;
			}
			toaster.pop({
				type: 'warning',
				title: title,
				body: msg,
				timeout: timeout
			});
		};
	}
]);
})();
