sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
	var headers = request.headers;
	var token = headers['cvtoken'];
	var endpoint = headers['cvendpoint'];
	var queryParams = request.queryParams; 
	var entityType = queryParams.entityType;
	var entityTypes = {};
	entityTypes.ClientEntity = {endpoint: "Client?hiddenclients=true", respIndex: "clientProperties"};
	entityTypes.ClientGroupEntity = {endpoint: "ClientGroup", respIndex: "groups"};
	entityTypes.StoragePolicyEntity = {endpoint: "StoragePolicy", respIndex: "policies"};
	//entityTypes.MA_TYPE = {name: "MediaAgentEntity", endpoint: "qcommand/qoperation execute"};
	entityTypes.UserEntity = {endpoint: "User", respIndex: "users"};
	entityTypes.UserGroupEntity = {endpoint: "UserGroup", respIndex: "userGroups"};
	entityTypes.SchedulePolicyEntity = {endpoint: "SchedulePolicy", respIndex: "taskDetail"};

	var entityTypeObj = entityTypes[entityType];
	var api = null;
	if(entityTypeObj){
		api = entityTypeObj.endpoint;
	}
	var responseJson = {};
	if(api != null){
		var sm = new sn_ws.RESTMessageV2();
		sm.setEndpoint(endpoint + "/webconsole/RestServlet/"+api);
		//var body = '<Workflow_GetExecutableWorkflowsRequest><workflow workflowId="'+workflowid+'"/></Workflow_GetExecutableWorkflowsRequest>';
		sm.setHttpMethod("get");
		sm.setHttpTimeout(80000);
		sm.setRequestHeader("Accept","application/json");
		sm.setRequestHeader("Content-Type","application/xml");
		sm.setRequestHeader("Authtoken", token);
		sm.setLogLevel("all");
		//sm.setRequestBody(body);
		var cvresponse = sm.execute();
		var cvresponseBody = cvresponse.getBody();
		var statusCode = cvresponse.getStatusCode();

		gs.info("statusCode " + statusCode);
		gs.info("responseBody ["+entityType+"]" + cvresponseBody); 

		if(statusCode != 200) {
			cvresponseBody = "{}";
		}
		//responseJson.respXML = cvresponseBody;
		var respJson = JSON.parse(cvresponseBody);
		gs.info('Response json: ' + JSON.stringify(respJson));
		responseJson.entities = respJson[entityTypeObj.respIndex];
		responseJson.cvStatusCode = statusCode;
	}else{
		responseJson.status = 500;
		responseJson.errorTxt = "Entity type: ["+entityType+"] not supported.";
	}

	return responseJson;


})(request, response);
