sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var headers = request.headers;
	var token = headers['cvtoken'];
	var endpoint = headers['cvendpoint'];

	var queryParams = request.queryParams; 
	var clientGroupId = queryParams.clientGroupId;

	gs.info('Getting clients for client group: ' + clientGroupId);

	//gs.info('Getting schedules using token ' + token);

	var sm = new sn_ws.RESTMessageV2();
	if(clientGroupId == 0) {
		sm.setEndpoint(endpoint + "/webconsole/RestServlet/client?propertylevel=10&Hiddenclients=false&includevm=true");	
	} else {
		sm.setEndpoint(endpoint + "/webconsole/RestServlet/client?clientGroupId=" + clientGroupId + "&propertylevel=10&Hiddenclients=false&includevm=true");
	}
	sm.setHttpMethod("get");
	sm.setHttpTimeout(80000);
	sm.setRequestHeader("Accept","application/json");
	sm.setRequestHeader("Authtoken", token);
	sm.setRequestHeader("mode", "EdgeMode");
	sm.setLogLevel("all");

	var cvresponse = sm.execute();
	var cvresponseBody = cvresponse.getBody();
	var statusCode = cvresponse.getStatusCode();

	gs.info("statusCode " + statusCode);
	gs.info("responseBody " + cvresponseBody); 

	if(statusCode != 200) {
		cvresponseBody = "{}";
	}

	var responseJson = JSON.parse(cvresponseBody);
	responseJson.cvStatusCode = statusCode;

	gs.info('Response json: ' + responseJson);

	return responseJson;

})(request, response);
